import React, {Component} from 'react';
import {View,Text,StyleSheet,TextInput,TouchableOpacity} from 'react-native';

class Register extends Component {
    constructor(){
        super();
        this.state = {
            selectedValue:''
           }
        this.handleChange = this.handleChange.bind(this);
      }
  
  renderSelectedForm(param) {
    switch(param) {
      case 'form_user':
        return (
            <View name="form_user" style={styles.action}>

            <Text style = {styles.textRegister}>E-mail</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='E-mail'
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />

            <Text style = {[styles.textRegister,{marginTop:35}]}>UserID</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='UserID'
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />

            <Text style = {[styles.textRegister,{marginTop:35}]}>Password</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Password'
                    secureTextEntry={true}
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
            <Text style = {[styles.textRegister,{marginTop:35}]}>Piano Abbonamento</Text>
                <select style={{marginTop:15, padding:5, borderRadius: 10}}>
                    <option value="standard">Piano Standard</option>
                    <option value="premium">Piano Premium</option>
                    <option value="live">Piano con Live</option>
                </select>

            <TouchableOpacity style={[styles.appButtonContainer,{marginTop:50}]}>
               <Text style={styles.appButtonText}>Registrati</Text>
            </TouchableOpacity>
            
            </View>);
      case 'form_pt':
        return (
            <View name="form_pt" style={styles.action}>
            <Text style = {styles.textRegister}>UserID</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Username'
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />

            <Text style = {[styles.textRegister,{marginTop:35}]}>Password</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Password'
                    secureTextEntry={true}
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
            <Text style = {[styles.textRegister,{marginTop:35}]}>E-mail</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='E-mail'
                    secureTextEntry={true}
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
            
            </View>);
      case 'form_nutri':
        return (
            <View name="form_nutri" style={styles.action}>
            <Text style = {styles.textRegister}>UserID</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Username'
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />

            <Text style = {[styles.textRegister,{marginTop:35}]}>Password</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Password'
                    secureTextEntry={true}
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
            <Text style = {[styles.textRegister,{marginTop:35}]}>E-mail</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='E-mail'
                    secureTextEntry={true}
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
            
            </View>);
      default:
        return null;
    }
  }
  
  handleChange(event) { this.setState({selectedValue: event.target.value}); }

    render() {
        return (
            <View style= {styles.container}>
                
                <View style={styles.divRegister}>
                    <Text style = {styles.textRegister}>Che tipo di utente sei?</Text>
                    <div>
                        <form >
                            <select style={{marginLeft: 25, padding:5, borderRadius: 10}} value={this.state.selectedValue} onChange={this.handleChange}>
                                <option value="form_user">Utente semplice</option>
                                <option value="form_pt">Personal Trainer</option>
                                <option value="form_nutri">Nutrizionista</option>
                            </select>
                        </form>
                    </div>
                 </View>
                 {this.renderSelectedForm(this.state.selectedValue)}
            </View>
        );
    }
}

export default Register;


const styles = StyleSheet.create({
    container: {
        flex: 3,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    textInput: {
        flex: 1,
        marginTop: 20,
        paddingLeft: 15,
        color: '#05375a',
    },
    textRegister: {
        color: '#05375a',
        fontSize: 18,
        fontWeight: "bold"
    },
    button: {
        alignItems: 'center',
        marginTop: 50
    },
    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },
      action: {
        marginTop: 25,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 7
    },
    divRegister: {
        flexDirection: 'row',
        marginTop: 25,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 7
    },
    divStyle: {
        borderRadius: 10,
    }
});
