import React, {Component} from 'react';
import {View,Text,StyleSheet,TextInput,TouchableOpacity} from 'react-native';

class Register extends Component {
    state = {}
    render() {
        return (
            <View style= {styles.container}>
                
                <View style={styles.divRegister}>
                    <Text style = {styles.textRegister}>Che tipo di utente sei?</Text>
                    <div>
                        <form >
                            <select style={{marginLeft: 25, marginTop: 10}} >
                                <option value="form_user">Utente semplice</option>
                                <option value="form_pt">Personal Trainer</option>
                                <option value="form_nutri">Nutrizionista</option>
                            </select>
                        </form>
                    </div>
                 </View>

                <View style={styles.action}>
                <Text style = {styles.textRegister}>UserID</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Username'
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
                </View>
                
                <View style={styles.action}>
                <Text style = {[styles.textRegister,{marginTop:35}]}>Password</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Password'
                    secureTextEntry={true}
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
                </View>


                <View style={styles.action}>
                <Text style = {[styles.textRegister,{marginTop:35}]}>E-mail</Text>
                <TextInput
                    style={styles.textInput}
                    placeholder='Email'
                    autoCapitalize="none"
                    placeholderTextColor="#666666"
                />
                </View>
                
                <View style={styles.button}>
                    <TouchableOpacity style={styles.appButtonContainer}>
                        <Text style={styles.appButtonText}>Registrati</Text>
                    </TouchableOpacity>
                </View>

            </View>
        );
    }
}

export default Register;


const styles = StyleSheet.create({
    container: {
        flex: 3,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    textInput: {
        flex: 1,
        marginTop: 20,
        paddingLeft: 15,
        color: '#05375a',
    },
    textRegister: {
        color: '#05375a',
        fontSize: 18,
        fontWeight: "bold"
    },
    button: {
        alignItems: 'center',
        marginTop: 50
    },
    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#009688",
        borderRadius: 10,
        paddingVertical: 10,
        paddingHorizontal: 12
      },
      appButtonText: {
        fontSize: 18,
        color: "#fff",
        fontWeight: "bold",
        alignSelf: "center",
        textTransform: "uppercase"
      },
      action: {
        marginTop: 25,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 7
    },
    divRegister: {
        flexDirection: 'row',
        marginTop: 25,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 7
    },
    divStyle: {
        borderRadius: 10,
    }
});
